import { Component } from '@angular/core';
import { UserService } from 'src/app/core';
import {
  Group,
  GroupCreate,
  GroupDelete,
  GroupEdit,
  GroupResponse,
} from 'src/app/core/interfaces/group.interface';
import { ColumnTable } from 'src/app/core/interfaces/sidebar.links.interface';
import { GroupService } from 'src/app/core/services/group.service';
import { PrimeIcons } from 'primeng/api';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-groups',
  templateUrl: './groups.component.html',
})
export class GroupsComponent {
  private groups: Group[] = [];
  get getGroups(): Group[] {
    return [...this.groups];
  }
  visibleModal: boolean = false;
  deleteModal: boolean = false;
  label: string = 'Crear';
  header: string = '';
  action: string = '';
  message: string = '';
  messageResponse: string = '';
  rowCreate: GroupCreate = {
    nombre: '',
    color: '',
  };
  rowSelectedEdit: GroupEdit = {
    _id: '',
    nombre: '',
    color: '',
    creador_id: '',
    modificador_id: '',
    actualizado_a: new Date(),
  };
  rowSelectedDelete: GroupDelete = { _id: ''};
  columns: ColumnTable[] = [
    {
      name: 'Id',
      key: '_id',
      show: false,
      isAvailableOnCreation: true,
      isAvailableOnEdit: true,
    },
    {
      name: 'Nombre',
      key: 'nombre',
      show: true,
      isAvailableOnCreation: true,
      isAvailableOnEdit: true,
    },
    {
      name: 'Color',
      key: 'color',
      show: true,
      isAvailableOnCreation: true,
      isAvailableOnEdit: true,
    },
    {
      name: 'Creador',
      key: 'creador_id',
      show: false,
      isAvailableOnCreation: true,
      isAvailableOnEdit: true,
    },
    {
      name: 'modificador',
      key: 'modificador_id',
      show: false,
      isAvailableOnCreation: true,
      isAvailableOnEdit: true,
    },
    {
      name: 'Creado',
      key: 'creado_a',
      show: true,
      isAvailableOnCreation: true,
      isAvailableOnEdit: true,
    },
    {
      name: 'Actualizado',
      key: 'actualizado_a',
      show: true,
      isAvailableOnCreation: true,
      isAvailableOnEdit: true,
    },
    {
      name: 'Acciones',
      key: 'acciones',
      show: true,
      isAvailableOnCreation: true,
      isAvailableOnEdit: true,
      hasEditButton: true,
      hasRemoveButton: true,
      hasCreateButton: true,
      edit: (row: GroupEdit) => {
        this.rowSelectedEdit = row;
        this.visibleModal = true;
        this.label = 'Guardar Cambios';
        this.header = 'Editar grupo';
        this.action = 'edit';
      },
      remove: (row: GroupDelete) => {
        this.rowSelectedDelete = row;
        this.label = 'Eliminar';
        this.header = 'Eliminar grupo';
        this.action = 'delete';
        this.message = `¿Está seguro que desea eliminar el grupo: ${row._id}?`;
        this.deleteModal = true;
      },
      create: () => {
        this.action = 'create';
        this.visibleModal = true;
        this.header = 'Crear grupo';
        this.label = 'Crear';
      },
      createIcon: PrimeIcons.PLUS,
      editIcon: PrimeIcons.PENCIL,
      removeIcon: PrimeIcons.TRASH,
    },
  ];
  

  constructor(
    private userService: UserService,
    private groupService: GroupService,
    private toastr: ToastrService
  ) {}

  ngOnInit(): void {
    this.userService.populate();
    this.fetchGroups();
  }

  fetchGroups(): void {
    this.groupService.getGroups().subscribe({
      next: (response) => {
        this.groups = this.materializeGroups(response);

      },
      error: (error) => console.error(error),
    });
  }

  createNewRecord(): void {
    this.action = 'create';
    this.visibleModal = true;
    this.header = 'Crear grupo';
    this.label = 'Crear';
  }

  filteredColums(): ColumnTable[] {
    let filteredColumns = this.columns.filter((column: ColumnTable) => column.show === true);

    return filteredColumns
  };

  materializeGroups(groups: GroupResponse[]): Group[] {
    const groupsMaterialized: Group[] = [];
    groups.forEach((group) => {
      groupsMaterialized.push({
        _id: group._id,
        nombre: group.nombre,
        color: group.color,
        creador_id: group.creador_id,
        modificador_id: group.modificador_id,
        creado_a: new Date(group.creado_a).toLocaleDateString('es-ES'),
        actualizado_a: new Date(group.actualizado_a).toLocaleDateString('es-ES'),
      });
    }
    );
    return groupsMaterialized;
  }

  onSubmitGroup(): void {
    this.visibleModal = false;
    this.deleteModal = false;
    if (this.action === 'edit') {
      this.groupService.editGroup(this.rowSelectedEdit).subscribe({
        next: (response) => {
          this.toastr.success(response.message, 'Éxito');
        },
        error: (error) => this.toastr.error(error?.error?.message, 'Error'),
      });
      this.rowSelectedEdit = {} as GroupEdit;
    }
    if (this.action === 'create') {
      this.groupService.createGroup(this.rowCreate).subscribe({
        next: (response) => {
          this.toastr.success(response.message, 'Éxito');
          this.fetchGroups();
        },
      });
      this.rowCreate = {} as GroupCreate;
    }
    if(this.action === 'delete'){
      this.groupService.deleteGroup(this.rowSelectedDelete._id).subscribe({
        next: (response) => {
          this.toastr.success(response.message, 'Éxito');
        },
        error: (error) => this.toastr.error(error?.error?.message, 'Error'),
      });
      this.rowSelectedDelete = {} as GroupDelete;
    }
    this.fetchGroups();
  }

  getObjectByAction(): GroupEdit | GroupCreate {
    return this.action === 'edit'  ? this.rowSelectedEdit : this.rowCreate;
  }

  columnsToDeleteByEdit: string[] = ['acciones', 'creado_a', 'actualizado_a', 'creador_id', 'modificador_id'];
  columnsToDeleteByCreate: string[] = ['acciones', 'creado_a', 'actualizado_a', 'creador_id', 'modificador_id', '_id'];
  getColumnsByAction(): ColumnTable[] {
    if(this.action === 'edit'){
      return this.columns.filter((column: ColumnTable) => !this.columnsToDeleteByEdit.includes(column.key));
    }
    return this.columns.filter((column: ColumnTable) => !this.columnsToDeleteByCreate.includes(column.key));
  }

}
<div class="banner">
  <h3 class="text-banner">Grupos</h3>
</div>
<div class="container">
  <div class="card border-0 shadow centered-page">
  <div class="card-body p-5"> 

  <section class="table-responsive-md">
    <app-table [columns]="filteredColums()" [data]="getGroups" [classes]="'table table-bordered table-striped table-hover'" ></app-table>
  </section>

  <section>
    <p-button (onClick)="createNewRecord()">Crear Grupo</p-button>
  </section>

  <p-dialog
    [(visible)]="visibleModal"
    [transitionOptions]="'300ms'"
    [modal]="true"
    [header]="header"
    [draggable]="false"
  >
    <form
      class="form-group p-3 rounded"
      style="width: 500px; background: #071426;"
    >
      <section class="container-fields">
        <div
          class="d-flex flex-wrap gap-3 justify-content-center align-items-center"
        >
          <div *ngFor="let column of getColumnsByAction()">
            <label class="form-label item">
              {{ column.name }}
              <input
                class="form-control"
                type="text"
                [name]="column.key"
                [(ngModel)]="getObjectByAction()[column.key]"
                [ngModelOptions]="{ standalone: true }"
                [disabled]="column.key === '_id'"
              />
            </label>
          </div>
        </div>
      </section>
    </form>
    
    <ng-template pTemplate="footer">
      <p-button
        type="button"
        [label]="label"
        class="p-button-success"
        (onClick)="onSubmitGroup()"
      ></p-button>
      <button
        pButton
        type="button"
        label="Cancelar"
        class="p-button-danger"
        (click)="visibleModal = false"
      ></button>
    </ng-template>
  </p-dialog>

  <p-dialog
    [(visible)]="deleteModal"
    transitionOptions="300ms"
    [modal]="true"
    [header]="header"
    [draggable]="false"
  >
    {{ message }}

    <ng-template pTemplate="footer">
      <p-button
        type="button"
        [label]="label"
        class="p-button-danger"
        (onClick)="onSubmitGroup()"
      ></p-button>
      <button
        pButton
        type="button"
        label="Cancelar"
        class="p-button-secondary"
        (click)="deleteModal= false"
      ></button>
    </ng-template>
  </p-dialog>
    </div>
  </div>
</div>
