_ngcontent-%COMP%] > .column[_ngcontent-%COMP%]:last-child {\r\n    text-align: right\r\n}\r\n\r\n.comment[_ngcontent-%COMP%]   .comment-meta[_ngcontent-%COMP%] {\r\n    color: #9da9b9;\r\n    font-size: 13px\r\n}\r\n\r\n.content-meta[_ngcontent-%COMP%] {\r\n    color: #717171;\r\n    font-size: .8rem\r\n}\r\n\r\n.comment[_ngcontent-%COMP%]   .reply-link[_ngcontent-%COMP%] {\r\n    transition: color .3s;\r\n    color: #606975;\r\n    font-size: 14px;\r\n    font-weight: 500;\r\n    letter-spacing: .07em;\r\n    text-transform: uppercase;\r\n    text-decoration: none\r\n}\r\n\r\n.comment[_ngcontent-%COMP%]   .reply-link[_ngcontent-%COMP%] > i[_ngcontent-%COMP%] {\r\n    display: inline-block;\r\n    margin-top: -3px;\r\n    margin-right: 4px;\r\n    vertical-align: middle\r\n}\r\n\r\n.comment[_ngcontent-%COMP%]   .reply-link[_ngcontent-%COMP%]:hover {\r\n    color: #0da9ef\r\n}\r\n\r\n.comment.comment-reply[_ngcontent-%COMP%] {\r\n    margin-top: 30px;\r\n    margin-bottom: 0\r\n}\r\n\r\n@media (max-width: 576px) {\r\n    .comment[_ngcontent-%COMP%] {\r\n        padding-left: 0\r\n    }\r\n    .comment[_ngcontent-%COMP%]   .comment-author-ava[_ngcontent-%COMP%] {\r\n        display: none\r\n    }\r\n    .comment[_ngcontent-%COMP%]   .comment-body[_ngcontent-%COMP%] {\r\n        padding: 15px\r\n    }\r\n    .comment[_ngcontent-%COMP%]   .comment-body[_ngcontent-%COMP%]::before, .comment[_ngcontent-%COMP%]   .comment-body[_ngcontent-%COMP%]::after {\r\n        display: none\r\n    }\r\n\r\n}\r\n\r\n.mt-10[_ngcontent-%COMP%] {\r\n    margin-top: 0.80rem;\r\n}\r\n\r\n.capitalize[_ngcontent-%COMP%] {\r\n    text-transform:capitalize;\r\n}\r\n\r\n.grid-dropdown[_ngcontent-%COMP%] {\r\n    display: grid;\r\n    grid-template-columns: 1fr 1fr;\r\n    grid-gap: 1rem;\r\n    justify-content: center;\r\n    height: 220px; width: 480px;\r\n    margin: .5rem 1rem 0  1rem;\r\n}\r\n\r\n.statusText[_ngcontent-%COMP%] {\r\n    background-color: #091a32; \r\n    font-weight: bold; \r\n    padding: 5px 20px; \r\n    border-radius: 30px;\r\n}\r\n.priority-text[_ngcontent-%COMP%] {\r\n    background-color: #091a32; \r\n    font-weight: bold; \r\n    padding: 5px 20px; \r\n    border-radius: 30px;\r\n}\r\n\r\n.hover-link[_ngcontent-%COMP%] {\r\n    transition: all 200ms ease-in-out;\r\n}\r\n\r\n.hover-link[_ngcontent-%COMP%]:hover {\r\n    color: #0d98c7;\r\n    border-left: 4px solid #0d98c7;\r\n}\r\n\r\n.attachment-group[_ngcontent-%COMP%] {\r\n    margin-top: 0.5rem; \r\n    border:1px solid rgb(209, 209, 209); \r\n    border-radius: 3px;\r\n    background-color: white;\r\n    overflow: visible;\r\n    height: auto;\r\n}\r\n\r\n.attachment-section[_ngcontent-%COMP%] {\r\n    padding-left: 2rem;\r\n}\r\n\r\n.attachment-creado[_ngcontent-%COMP%] {\r\n    font-size: small; \r\n    color: rgb(130, 114, 95); \r\n    border-bottom: 1px solid rgba(244, 166, 32, 0.452); \r\n    max-width: 95%;\r\n}\r\n\r\n.attachment-nombre[_ngcontent-%COMP%] {\r\n    cursor: pointer; \r\n    margin: 0; \r\n    color: rgb(34, 94, 133); \r\n    padding-left: 0.50rem;\r\n}\r\n\r\n.attachment-no[_ngcontent-%COMP%] {\r\n    padding-left: 2rem; \r\n    color: rgb(34, 94, 133);\r\n}\r\n\r\n.attachment-titulo[_ngcontent-%COMP%] {\r\n    margin: 0%; \r\n    padding-top: 1rem; \r\n    padding-bottom: 0.50rem; \r\n    padding-left: 1rem; \r\n    font-size:large; \r\n    font-weight: bold;\r\n}\r\n\r\n.input-file[_ngcontent-%COMP%]{\r\n    max-width: 33rem; \r\n    margin-right: 1rem;\r\n}\r\n\r\ninput[_ngcontent-%COMP%] {\r\n    display: flex;\r\n    justify-content: center;\r\n    border-color: white;\r\n}\r\n\r\ninput[_ngcontent-%COMP%]::file-selector-button {\r\n    font-weight: bold;\r\n    color: rgb(246, 247, 248);\r\n    border: none;\r\n    border-radius: 3px;\r\n    background-color: #1863c6;\r\n    margin-top: 0;\r\n\r\n}\n/*# sourceMappingURL=data:application/json;charset=utf-8;base64, */"]
